import React from "react";
import { Redirect } from "react-router-dom";
import { useState } from "react";
const { Provider, Consumer } = React.createContext();
function Protected(props) {
  let { updateloginStatus } = props;
  const [state, setState] = React.useState({
    updateloginStatus: "",
  });

  console.log("protected component ");
  const Cmp = props.component;

  var Get_Local_object = JSON.parse(localStorage.getItem("logged_user"));
  var username = Get_Local_object["username"];
  if (username === "undefined" || username === "" || username === undefined) {
    var Auth = false;
  } else {
    var Auth = true;
    // props.updateloginStatus(true);
  }

  console.log("Auth = " + Auth);
  return (
    <div>
      {Auth ? (
        <Cmp onLoad={(Auth) => props.updateloginStatus(Auth)} />
      ) : (
        <Redirect to="/login"></Redirect>
      )}
    </div>
  );
}
export default Protected;
