import React from "react";
import { withRouter } from "react-router-dom";
import "./style.css";
export default class Login extends React.Component {
  constructor(props) {
    super();
    this.state = {
      username: "",
      pasword: "",
    };
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.routeChange = this.routeChange.bind(this);
  }
  onChange(e) {}
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  routeChange = () => {
    let path = `/signup`;
    this.props.history.push(path);
  };
  submitForm(e) {
    e.preventDefault();
    const { username, password } = this.state;
    console.log(this.state);
    if (username === "ahmed" && password === "ahmed") {
      console.log("successfull");
      console.log(this.props);
      localStorage.setItem("username", username);
      localStorage.setItem("password", password);
    } else {
      console.log("failed");
    }
    //loginmagic
  }
  render() {
    return (
      <div className="row">
        <div className="col-md-2"></div>
        <div className="col-md-8">
          <div className="row">
            <div className="col-md-4 border-danger border bg-success">
              <div className="row">
                <div className="col-md-1"></div>
                <div className="col-md-10 ">
                  {/* start side bar content  */}
                  <div className="row ">
                    <div className="col-md-12 text-center text-white">
                      <h2>Welcome </h2>
                      <p className="font-weight-bold">Not already Registered</p>
                      <p className="font-weight-bold">
                        Create a free account now{" "}
                      </p>
                    </div>

                    <div className="col-md-12 text-center text-white">
                      <button
                        type="button"
                        className="btn btn-light btn-block "
                      >
                        Signup
                      </button>
                    </div>
                  </div>
                  {/* end side bar content  */}
                </div>
                <div className="col-md-1"></div>
              </div>
            </div>
            <div className="col-md-8 bg-dark">
              {/* start card form  */}
              <div className="card">
                <div className="card-header text-center ">Login</div>
                <div className="card-body">
                  <form>
                    <div className="form-row form-inline">
                      <input
                        type="text"
                        name="username"
                        id="username"
                        placeholder="user name"
                        className="form-control"
                        onChange={this.handleChange}
                      />
                    </div>
                    <div className="form-row">
                      <input
                        type="password"
                        name="password"
                        id="password"
                        placeholder="password"
                        className="form-control"
                        onChange={this.handleChange}
                      />
                    </div>
                    <div className="form-row">
                      <input
                        type="submit"
                        name="submit"
                        onClick={this.submitForm}
                        id="submit"
                      />
                    </div>
                  </form>
                </div>
                <div className="card-footer">
                  <span>Have no account .... </span>
                  <button
                    className="btn btn-warning"
                    onClick={this.routeChange}
                  >
                    Sign Up First{" "}
                  </button>
                </div>
              </div>
              {/* end card form  */}
            </div>
          </div>
        </div>
        <div className="col-md-2"></div>
      </div>
    );
  }
}
