import React from "react";
import { useForm } from "react-hook-form";
export default function Signup() {
  const { register, handleSubmit, errors } = useForm();

  const addUser = (data) => {
    let first_name = data.first_name;
    let last_name = data.last_name;
    let email = data.email;
    let username = data.username;
    let password = data.password;

    fetch("http://localhost:3001/api/users/add", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        first_name: first_name,
        last_name: last_name,
        email: email,
        username: username,
        password: password,
      })
        .then((result) => result.json())
        .then((info) => {
          console.log(info);
        }),
    });
  };
  const submit = (data) => {
    console.log(data);
    addUser(data);
  };
  return (
    <div className="row">
      <div className="col-md-3"></div>
      <div className="col-md-6">
        <form onSubmit={handleSubmit(submit)}>
          <div className="form-row">
            <label className="btn ">first name</label>
            <input
              type="text"
              name="first_name"
              className="form-control"
              ref={register({
                required: "{errors.password.message}",
                minLength: {
                  value: 5,
                  message: "First name should have minimum 5 charachters ",
                },
                maxLength: {
                  value: 14,
                  message: "First name should have maximum 14 charachters ",
                },
              })}
            />
            {errors.first_name && (
              <small className="text-info">{errors.first_name.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">last name</label>
            <input
              type="text"
              name="last_name"
              className="form-control"
              ref={register({
                required: "Last name is required",
                minLength: {
                  value: 5,
                  message: "Last name should have minimum 5 charachters ",
                },
                maxLength: {
                  value: 14,
                  message: "Last name should have maximum 14 charachters ",
                },
              })}
            />
            {errors.last_name && (
              <small className="text-info">{errors.last_name.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              ref={register({ required: "Email is required" })}
            />
            {errors.email && (
              <small className="text-info">{errors.email.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">username</label>
            <input
              type="text"
              name="username"
              className="form-control"
              ref={register({
                required: "Username name is required",
                minLength: {
                  value: 5,
                  message: "Username should have minimum 5 charachters ",
                },
                maxLength: {
                  value: 14,
                  message: "Username should have maximum 14 charachters ",
                },
              })}
            />
            {errors.username && (
              <small className="text-info">{errors.username.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              ref={register({
                required: "Password is required",
                minLength: {
                  value: 8,
                  message: "Password should have minimum 8 charachters ",
                },
                maxLength: {
                  value: 14,
                  message: "Password should have maximum 14 charachters ",
                },
              })}
            />
            {errors.password && (
              <small className="text-info">{errors.password.message}</small>
            )}
          </div>
          <input
            type="submit"
            className="btn btn-sm btn-success"
            name="submit"
          />
        </form>
      </div>
      <div className="col-md-3"></div>
    </div>
  );
}
