import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers";
import * as yup from "yup";
const schema = yup.object().shape({
  first_name: yup
    .string({ message: "First Name must be string " })
    .min({ min: 5, message: "First Name must be atleast 5 charachters " })
    .max({ max: 14, message: "First Name must be atmost 14 chrachters " })
    .required({ message: "First Name is required" }),
  last_name: yup
    .string({ message: "Last Name must be string " })
    .min({ min: 5, message: "Last Name must be atleast 5 charachters " })
    .max({ max: 14, message: "Last Name must be atmost 14 chrachters " })
    .required({ message: "Last Name is required" }),
  email: yup
    .string()
    .email()
    .min({ min: 8, message: "Email must be atleast 5 charachters " })
    .max({ max: 20, message: "Email must be atmost 14 chrachters " })
    .required({ message: "Email is required" }),
  password: yup
    .string({ message: "Password must be string " })
    .min({ min: 5, message: "Password must be atleast 5 charachters " })
    .max({ max: 14, message: "Password must be atmost 14 chrachters " })
    .required({ message: "Password is required" }),
  username: yup
    .string({ message: "Username must be string " })
    .min({ min: 5, message: "Username must be atleast 5 charachters " })
    .max({ max: 14, message: "Username must be atmost 14 chrachters " })
    .required({ message: "Username is required" }),
  //last_name: yup.number().positive().integer().required(),
});
export default function Signup() {
  const { register, handleSubmit, errors } = useForm({
    resolver: yupResolver(schema),
  });

  const addUser = (data) => {
    let first_name = data.first_name;
    let last_name = data.last_name;
    let email = data.email;
    let username = data.username;
    let password = data.password;

    fetch("http://localhost:3001/api/users/add", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        first_name: first_name,
        last_name: last_name,
        email: email,
        username: username,
        password: password,
      }),
    })
      .then(function (res) {
        console.log(res);
        if (res.status === 200) {
          console.log("success");
        } else {
          console.log("failed");
        }
      })
      .catch(function (err) {
        console.log(err);
      });
  };

  const submit = (data) => {
    console.log(data);
    addUser(data);
  };
  return (
    <div className="row">
      <div className="col-md-3"></div>
      <div className="col-md-6">
        <form onSubmit={handleSubmit(submit)}>
          <div className="form-row">
            <label className="btn ">first name</label>
            <input
              type="text"
              name="first_name"
              className="form-control"
              ref={register}
            />
            {errors.first_name && (
              <small className="text-info">{errors.first_name?.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">last name</label>
            <input
              type="text"
              name="last_name"
              className="form-control"
              ref={register}
            />
            {errors.last_name && (
              <small className="text-info">{errors.last_name?.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              ref={register}
            />
            {errors.email && (
              <small className="text-info">{errors.email?.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">username</label>
            <input
              type="text"
              name="username"
              className="form-control"
              ref={register}
            />
            {errors.username && (
              <small className="text-info">{errors.username?.message}</small>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              ref={register}
            />
            {errors.password && (
              <small className="text-info">{errors.password?.message}</small>
            )}
          </div>
          <input
            type="submit"
            className="btn btn-sm btn-success"
            name="submit"
          />
        </form>
      </div>
      <div className="col-md-3"></div>
    </div>
  );
}
