import React from "react";
import { withRouter } from "react-router-dom";
import "./style.css";
import Cover_Image from "./../assets/background_1.png";
import Login_Compoenent_Background from "./../assets/background_3.jpg";
import Cover_Image_3 from "./../assets/background_2.jpg";

export default class Login extends React.Component {
  constructor(props) {
    super();
    this.state = {
      username: "",
      pasword: "",
    };
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.routeChange = this.routeChange.bind(this);
  }
  onChange(e) {}
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  routeChange = () => {
    let path = `/signup`;
    this.props.history.push(path);
  };
  submitForm(e) {
    e.preventDefault();
    const { username, password } = this.state;
    console.log(this.state);
    if (username === "ahmed" && password === "ahmed") {
      console.log("successfull");
      console.log(this.props);
      localStorage.setItem("username", username);
      localStorage.setItem("password", password);
    } else {
      console.log("failed");
    }
    //loginmagic
  }
  render() {
    console.log(Cover_Image);
    return (
      <div
        className="row align-items-center login-component-wrapper"
        style={{
          backgroundImage: "url(" + Login_Compoenent_Background + ")",
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
        }}
      >
        <div className="col-md-2"></div>
        <div className="col-md-8 ">
          <div className="row ">
            <div
              className="col-md-4 sidebar-wrapper "
              style={{
                backgroundImage: "url(" + Cover_Image + ")",
                backgroundPosition: "center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
              }}
            >
              {/* start side bar content  */}

              <small className="font-weight-bold text-white">
                Not already Registered
              </small>
              <small className="font-weight-bold text-white">
                Create a free account now{" "}
              </small>
              <div className="d-flex"></div>
              <button
                type="button"
                id="signupBtn"
                className="btn btn-warning btn-block "
                onClick={this.routeChange}
              >
                <i
                  class="fa fa-user-plus fa-2x text-white"
                  aria-hidden="true"
                ></i>
              </button>
            </div>
            {/* end side bar content  */}

            <div
              className="col-md-8 bg-dark form-wrapper"
              style={{
                backgroundImage: "url(" + Cover_Image_3 + ")",
                backgroundPosition: "center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
              }}
            >
              {/* start card form  */}

              <h2 className="font-weight-bold text-success">Login Now </h2>
              <div className="d-flex flex-row justify-content-center align-items-center mt-3 mb-3">
                <span className="ml-2 mr-2 ">
                  <i
                    className=" fa-2x text-primary fa fa-facebook-official"
                    aria-hidden="true"
                  ></i>
                </span>
                <span className="ml-2 mr-2 ">
                  <i
                    className=" fa-2x text-success fa fa-google-plus-official"
                    aria-hidden="true"
                  ></i>
                </span>
                <span className="ml-2 mr-2 ">
                  <i
                    className=" fa-2x text-info fa fa-twitter-square"
                    aria-hidden="true"
                  ></i>
                </span>
              </div>
              <form>
                <div className="form-row form-inline">
                  <input
                    type="text"
                    name="username"
                    id="username"
                    placeholder="user name"
                    className="form-control custom-login-input"
                    onChange={this.handleChange}
                  />
                </div>
                <div className="form-row form-inline">
                  <input
                    type="password"
                    name="password"
                    id="password"
                    placeholder="password"
                    className="form-control custom-login-input"
                    onChange={this.handleChange}
                  />
                </div>
                <div className="text-center">
                  <button
                    type="submit"
                    name="submit"
                    className="btn btn-success"
                    onClick={this.submitForm}
                    id="submit"
                  >
                    <i class="fa fa-sign-in fa-2x" aria-hidden="true"></i>
                  </button>
                </div>
              </form>

              {/* end card form  */}
            </div>
          </div>
        </div>
        <div className="col-md-2"></div>
      </div>
    );
  }
}
