import React from "react";
import { useForm } from "react-hook-form";
export default function Signup() {
  const { register, handleSubmit, errors } = useForm();
  const submit = (data) => {
    console.log(data);
  };
  return (
    <div className="row">
      <div className="col-md-3"></div>
      <div className="col-md-6">
        <form onSubmit={handleSubmit(submit)}>
          <div className="form-row">
            <label className="btn ">first name</label>
            <input
              type="text"
              name="first_name"
              className="form-control"
              ref={register({
                required: "First name is required",
                minLength: 5,
                maxLength: 14,
              })}
              required
            />
            {errors.first_name && (
              <p className="text-info">first name is required</p>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">last name</label>
            <input
              type="text"
              name="last_name"
              className="form-control"
              ref={register({
                required: "Last name is required",
                minLength: 5,
                maxLength: 14,
              })}
              required
            />
            {errors.last_name && (
              <p className="text-info">first name is required</p>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              ref={register({ required: "Email is required" })}
              required
            />
            {errors.email && (
              <p className="text-info">first name is required</p>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">username</label>
            <input
              type="text"
              name="username"
              className="form-control"
              ref={register({ required: true, minLength: 5, maxLength: 14 })}
              required
            />
            {errors.username && (
              <p className="text-info">first name is required</p>
            )}
          </div>
          <div className="form-row">
            <label className="btn ">password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              ref={register({ required: true, minLength: 5, maxLength: 14 })}
              required
            />
            {errors.password && (
              <p className="text-info">first name is required</p>
            )}
          </div>
          <input
            type="submit"
            className="btn btn-sm btn-success"
            name="submit"
          />
        </form>
      </div>
      <div className="col-md-3"></div>
    </div>
  );
}
